import React from 'react'
import "./comment.css"
const Comment = ({ comment } ) => {
  var utcSeconds = comment.date;
var d = new Date(0); // The 0 there is the key, which sets the date to the epoch
d.setUTCSeconds(utcSeconds);
  console.log(d);
  return (
    <div className='cc'>
      <p>{comment.name}</p>
      <p>{comment.comm}</p>
      <p></p>
    </div>
  )
}

export default Comment
