  console.log(ndate) ;

  const [date , setDate] = useState("") ; 
   const [nam ,setnam] = useState("");
   const [com ,setcom ] =useState("") ;
useEffect(()=>{
setDate(ndate) ;
setnam(comment.name) ;
setcom(comment.comm) ;
}, [comment])

  return (
    <div className='cc'>
      <p>{date}</p>
      <p></p>
      <p></p>
    </div>
  )
}

