import axios from "axios";
import {useContext, useEffect, useState} from "react";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { Context } from "../../context/Context";
import "./singlePost.css";

export default function SinglePost() {
    const location =useLocation();
    const path=(location.pathname.split("/")[2]);
    const [post,setPost]=useState({});
  const PF="http://localhost:5000/images/";
  const {user}=useContext(Context);
    const [title,setTitle]=useState("")
    const [desc,setDesc]=useState("")
    const [updateMode,setUpdateMode]=useState(false)
    const [comment  , setcomment] = useState("") ;
    const [com , setcom] = useState([]) ;


    useEffect(()=>{
const getPost =async ()=>{
    const res= await axios.get("/posts/"+path);
    setPost(res.data);
    setTitle(res.data.title);
    setDesc(res.data.desc);
    setcom(res.data.comments);
};
getPost();
    },[path]);

const handleDelete = async()=>{
    try{
    await axios.delete(`/posts/${post._id}`,{data:{username:user.username},});
    window.location.replace("/");
    }catch(err){}
}
const handleUpdate = async ()=>{
    try{
        await axios.put(`/posts/${post._id}`,{
        username:user.username,
        title,
        desc,
     }
    );
        setUpdateMode(false);
        }catch(err){}
}
const handlecomment =async()=>{
    try
    {
        const cdata = {name:user.username , comm: comment , date : new Date()} ; 
      const data = await axios.patch(`/posts/${post._id}` , {
       $push:{comments: cdata}
       }) 
       console.log(data)
    }
    catch(err)
    {
        console.log(err)
    }
}
console.log(comment);
    return (
        <>
        <div className="singlePost">
            <div className="singlePostWrapper">
                {post.photo &&(
                <img  src={PF+post.photo} alt="" className="singlePostImg" />
                )}
                {
                    updateMode ?
                    (<input type="text" 
                    value={title} 
                    className="singlePostTitleInput"
                    autoFocus
                    onChange={(e)=>setTitle(e.target.value)}
                    />)
                    :(
                <h1 className="singlePostTitle">
                 {title}
                 {post.username===user?.username &&(
                 <div className="singlePostEdit">
                 <i className=" singlePostIcon far fa-edit" onClick={()=>setUpdateMode(true)}></i>
                 <i className=" singlePostIcon far fa-trash-alt" onClick={handleDelete}></i>
                 </div>
                 )}
             </h1>
              )
            }
             <div className="singlePostInfo">
                 <span className="singlePostAuthor">Author: 
                 <Link to={`/?user=${post.username}`} className="link">
                 <b>{post.username}</b>
                 </Link>
                 </span>
                 <span className="singlePostDate">{new Date(post.createdAt).toDateString()}</span>
                 </div>
                 {updateMode ?
                 (
                 <textarea className="singlePostDesInput"
                  value={desc} onChange={(e)=>setDesc(e.target.value)}
                   autoFocus />
                 ):(
                 <p className="singlePostDes">
                    {desc}
                 </p>
             )}
             {updateMode && (
             <button className="singlePostButton" onClick={handleUpdate}>
                 Update
                 </button>
             )}</div>
              <div className="comment">
           <div className="comcon">
            <span><input placeholder="Write a comment..." type="text" className="cominput" onChange={(e)=>setcomment(e.target.value)}></input><button className="cb" onClick={handlecomment}>submit</button></span>
            </div> 
        </div>
        </div>
       
        </>
    );
}
